// n8n Code node
// Input: items[i].json = { Date, "Value Date", Debit, Credit, Label, Balance }
// Output: items = [{ json: {...} }, ...]

// List of keywords to check in the Label
const keywords = ["XXX", "YYY"];

// Function to convert a number to string with comma as decimal separator
function formatAmount(value) {
    if (value === null || value === undefined || value === "") return "";
    let num = parseFloat(value);
    if (isNaN(num)) return "";
    return num.toFixed(2).replace('.', ',');
}

// Iterate over all rows in the array
const output = $input.all().map(item => {
    const row = { ...item.json }; // Copy to avoid modifying the original

    // Check if the label contains any of the keywords
    const hasKeyword = keywords.some(keyword => row.Label.includes(keyword));
    if (hasKeyword) {
        // Transfer the amount from Debit to Credit
        row.Credit = row.Debit;
        row.Debit = "";
    }

    // Convert amounts to string with comma as decimal separator
    row.Debit = formatAmount(row.Debit);
    row.Credit = formatAmount(row.Credit);
    row.Balance = formatAmount(row.Balance);

    return { json: row };
});

// Return the full array in n8n format
return output;
